class GitSrvices {


  async getData(url){
    const body = await fetch(url);
    const jsonData = body.json();
    return jsonData
  };

  getUser (name){
    const res = this.getData(`https://api.github.com/users/${name}`);
    return res;
  }

  getRepo(name){
    const res = this.getData(`https://api.github.com/users/${name}/repos`);
    return res
  }

};

export default GitSrvices
