/*class UI {

  constructor(){
    this.p = document.querySelector('.withInput');

  }

  showError(message,className = 'alert alert-error'){

          const errMesage = document.createElement('div');
          errMesage.setAttribute('class',className);
          errMesage.textContent(message);
          this.p.insertAdjacentElement('beforebegin',errMesage)

  }



}

export default UI
*/
