class UI {

  constructor(){
    this.element = document.querySelector('.input');

  }

 showError = (message,className) =>{

   const div = '<div class="${className}">${message}</div>';
   this.element.insertAdjacentHTML('afterbegin' , div)

  }



}

export default UI
