class GitSrvices {


  async getData(url){
    const body = await fetch(url);
    const jsonData = body.json();
    return jsonData
  };

  getUser=async (name) =>{
    const res =await this.getData(`https://api.github.com/users/${name}`);
   
    return this.userTransform(res);
  }

  getRepo(name){
    const res = this.getData(`https://api.github.com/users/${name}/repos`);
    return res
  }


  userTransform = (user) =>{

    return {
      id :user.id,
      name : user.name,
      img:user.avatar_url,
      repos : user.public_repos,
      login : user.login

    }
}

};

export default GitSrvices
