class UI {

  constructor(){
    this.p = document.querySelector('.withInput');

  }

  showError(message = '',className = 'alert alert-error'){

          const errMesage = `<div class="${className}">"${message}"</div>`;

          this.p.insertAdjacentElement('aftebegin',errMesage)

  }



}

export default UI
