class UI {

  constructor(){
    this.p = document.getElementsByClassName('input');

  }

  showError(message = '',className = 'alert alert-error'){

    const  indiv = `<div> </div>`;
    this.p.insertAdjacentElement('afterbegin',indiv)


  }



}

export default UI
